var carcass = require('carcass');
var express = require('express');
var path = require('path');

var Resource = module.exports = carcass.constructor.extend();

Resource.prototype.bootstrap = function(attributes, options, callback) {
    this.app = express();
    callback();
};

// Helpers
// -------

// Mount my app to a given app.
Resource.prototype.mountTo = function(app, route) {
    route || (route = '/');
    if (this.route) {
        // FIXME
        route = path.resolve(route, this.route);
    }
    app.use(route, this.app);
};
